//Replaces a container's fragment(s)
FragmentTransaction ft = getFragmentManager().beginTransaction();
MyFragment newFrag =new MyFragment(); //TODO Change to your fragment constructor
ft.addToBackStack(null); 
//Allows this transaction to be undone when the back button is pressed ft.replace(R.id.containerId, newFrag, "newFragTag"); 
//TODO Replace 'R.id.containerId' (with the ID of a View) and 'newFragTag'
ft.commit();

//Adds a fragment to a specified layout
FragmentTransaction ft = getFragmentManager().beginTransaction();
MyFragment newFrag =new MyFragment(); 
//TODO Change to your fragment constructor ft.addToBackStack(null); 
//Allows this transaction to be undone when the back button is pressed
ft.add(R.id.layoutId, newFrag); 
//TODO Replace 'R.id.layoutId' (with the ID of a layout)
ft.commit();
